import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./dashboard.css";
import axios from "axios";

class DashRecipeItem extends Component {
  state = {
    title: this.props.title,
    description: this.props.description,
    image: this.props.image,
  };

  componentDidMount() {
    const imageRoute = this.state.image.replace("uploads", "");
    console.log(imageRoute);
    const route = "/uploads/" + imageRoute;
    console.log(route);
    axios.get(route).then((res) => this.setState({ image: res.data }));
  }
  render() {
    return (
      <div className="dashPopItem">
        <div className="dashPopItemContent">
          <div className="dashPopItemTitle">{this.state.title}</div>
          <div className="dashPopItemImg">
            <img src={this.state.image} alt="food" />
          </div>
          <div className="dashPopItemDesc">{this.state.description}</div>
        </div>
      </div>
    );
  }
}

export default DashRecipeItem;
