import React, { Component } from "react";
import { Redirect, Switch, Route } from "react-router-dom";
import axios from "axios";

import CreateFamilyPage from "./createfamily";

class FamilyPage extends Component {
  state = {
    family: "",
  };

  componentDidMount() {
    axios
      .get("/api/getfamily")
      .then((result) => this.setState({ family: result.data }));
  }

  render() {
    if (this.state.family === false) {
      return (
        <React.Fragment>
          <Switch>
            <Route path="/family/create" component={CreateFamilyPage} />
          </Switch>

          <Redirect to="/family/create" />
        </React.Fragment>
      );
    }
    return <div>Family Page!</div>;
  }
}

export default FamilyPage;
