import React, { Component } from "react";
import { Redirect, Switch, Route } from "react-router-dom";
import axios from "axios";

import CreateFamilyPage from "./createfamily";

class FamilyPage extends Component {
  state = {
    family: "",
  };

  componentDidMount() {
    axios
      .get("/api/getfamily")
      .then((result) => this.setState({ family: result.data }));
  }

  render() {
    if (this.state.family === false) {
      return <Redirect to="/dashboard/family/create" />;
    }

    return (
      <React.Fragment>
        <Switch>
          <Route path="dashboard/family/create" component={CreateFamilyPage} />
        </Switch>
        <React.Fragment>
          <div>Family</div>
        </React.Fragment>
      </React.Fragment>
    );
  }
}

export default FamilyPage;
