import React, { Component } from "react";
import { Redirect, Route } from "react-router";
import axios from "axios";

/*
class PrivateRoute extends Component {
  state = {
    authorized: "",
    component: this.props.component,
    route: this.props.route,
  };
  componentDidMount() {
    axios
      .get("/api/authorize")
      .then((result) => this.setState({ authorized: result.data }))
      .catch((error) => {
        console.log(error.response);
      });
  }
  render() {
    if (this.state.authorized === false) {
      return <Redirect to="/login" />;
    }
    return <Route path={this.state.route} component={this.state.component} />;
  }
}

export default PrivateRoute;
*/

checkAuth = () => {
  let isAuth = axios.get("/api/authorize");
  return isAuth.data;
};

const PrivateRoute = ({ component: Component, handleChildFunc, ...rest }) => {
  const user = "token from cookie";
  return (
    <Route
      {...rest}
      render={(props) =>
        checkAuth() !== false ? (
          <Component {...props} user={user} handleChildFunc={handleChildFunc} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );
};
export default PrivateRoute;
