import React, { Component } from "react";
import { Route, Redirect } from "react-router-dom";
import axios from "axios";

class PrivateRoute3 extends Component {
  state = {
    auth: "",
    component: this.props.component,
    path: this.props.path,
  };
  checkRender = () => {
    axios.get("/api/authorize").then((result) => {
      this.setState({ auth: result.data });
    });
    this.state.auth === true ? this.state.component : <Redirect to="/login" />;
  };

  render() {
    return <Route path={this.state.path} component={this.checkRender()} />;
  }
}

export default PrivateRoute3;
