import React, { Component } from "react";
import { Redirect } from "react-router-dom";

import "./createfamily.css";
import axios from "axios";

class CreateFamilyForm extends Component {
  state = {
    name: "",
    desc: "",
    url: "",
    checkJoin: undefined,
  };

  handleChange = (inputType) => (event) => {
    this.setState({ [inputType]: event.target.value });
  };

  handleCreate = () => {
    const data = {
      family: [this.state.name, this.state.desc],
    };
    if (this.state.name.length === 0 || this.state.desc.length === 0) {
      console.log("One or more fields is empty");
    } else {
      axios.post("/api/addfamily", data);
    }
  };

  handleJoin = () => {
    this.setState({ checkJoin: true });
  };

  fixLink = () => {
    const link = this.state.url;
    const newLink = link.replace("localhost:3000", "");
    return newLink;
  };

  render() {
    if (this.state.checkJoin === true) {
      window.location.reload(false);
      return <Redirect to={this.fixLink()} />;
    }
    return (
      <div id="createFamWrapper">
        <div>Content</div>
      </div>
    );
  }
}

export default CreateFamilyForm;
